--
-- Cấu trúc bảng cho bảng `pricing_comparisons`
--

CREATE TABLE `pricing_comparisons` (
  `id` int(11) NOT NULL,
  `feature_name` varchar(255) NOT NULL,
  `val_basic` varchar(255) DEFAULT NULL COMMENT 'Giá trị cột Basic',
  `val_pro` varchar(255) DEFAULT NULL COMMENT 'Giá trị cột Pro',
  `val_enterprise` varchar(255) DEFAULT NULL COMMENT 'Giá trị cột Enterprise',
  `sort_order` int(11) DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Đang đổ dữ liệu cho bảng `pricing_comparisons`
--

